import {
  CircularProgressbar,
  CircularProgressbarWithChildren,
  buildStyles
} from "react-circular-progressbar";
import { easeQuadInOut } from "d3-ease";
import AnimatedProgressProvider from "./AnimatedProgressProvider";
import "react-circular-progressbar/dist/styles.css";
import './Stats.css';

function Stats() {
  return (
    <div className="Stats">
        <AnimatedProgressProvider
        valueStart={0}
        valueEnd={66}
        duration={1.4}
        easingFunction={easeQuadInOut}
        repeat
      >
        {value => {
          const roundedValue = Math.round(value);
          return (
            <CircularProgressbar
              value={value}
              text={`${roundedValue}%`}
              styles={buildStyles({ 
                pathTransition: "none",
                textSize: '16px', 
              })}
            />
          );
        }}
      </AnimatedProgressProvider>
        <h1>Stats Placeholder...</h1>
        <p>Stats will include metrics like...</p>
        <br/>
        <p >Number of countries visited out of total countries</p>
        <p>Percentage of land area countries visited takes up</p>
        <p>Country that currently has the hottest/coldest temperature</p>
        <p>Country that currently has the highest/lowest population</p>
        <p>Country that currently has the highest/lowest GDP</p>
        <br/>
        <p>Metrics are planned to be implemented with the help of <a href="https://airbnb.io/visx">visx</a></p>      
    </div>
  )
}

  export default Stats
