// This component could use some cleaning up... Should get rid of all the [min_max] indexing
// More time effecient solution could be written for determining min and max GDP's

import { useState, useRef } from 'react'
import FlagStat from "./FlagStat";



function MinMax({ mapState, country_data, country_comp, text, min_max, comp_index }) {

    let target_gdp = useRef([[null, null], [null, null]])
    let comp_array = useRef([[], []])
    let flag_array = useRef([[], []])
    let prev_countries = useRef(['', ''])
    let prev_selected = useRef([false, false])

    const [targetCountry, setTargetCountry] = useState([0, '']);

    function calculateTarget() {
        let index
        prev_countries.current[min_max] = mapState.countryCode
        prev_selected.current[min_max] = mapState.isSelected

        let gdp = country_comp.find(i=>i.country === mapState.countryName)[comp_index]

        if (!mapState.isSelected) {
            index = comp_array.current[min_max].indexOf(gdp)
            comp_array.current[min_max] = comp_array.current[min_max].filter(function(e) { return e !== gdp })
            flag_array.current[min_max].splice(index, 1)

            if (comp_array.current[min_max].length === 0) {
                target_gdp.current[min_max] = [null, null]
                setTargetCountry([0, ''])
                return
            }

            if (gdp === target_gdp.current[min_max][0]) {
                let new_target_gdp

                if (min_max === 1) {
                    new_target_gdp = Math.max(...comp_array.current[min_max])
                } else {
                    new_target_gdp = Math.min(...comp_array.current[min_max])
                }

                index = comp_array.current[min_max].indexOf(new_target_gdp)
                target_gdp.current[min_max] = [new_target_gdp, flag_array.current[min_max][index]]
                setTargetCountry(target_gdp.current[min_max])
            }

            return
        }

        let flag = country_data.find(i=>i.cca2 === mapState.countryCode).flag
        comp_array.current[min_max].push(gdp)
        flag_array.current[min_max].push(flag)

        if (min_max === 1) {
            if (gdp > target_gdp.current[min_max][0] || !target_gdp.current[min_max][0]) {
                target_gdp.current[min_max] = [gdp, flag]
                setTargetCountry(target_gdp.current[min_max])
            }
        } else {
            if (gdp < target_gdp.current[min_max][0] || !target_gdp.current[min_max][0]) {
                target_gdp.current[min_max] = [gdp, flag]
                setTargetCountry(target_gdp.current[min_max])
            }
        }
    }


    if (mapState.countryCode !== '' && (mapState.countryCode !== prev_countries.current[min_max] || mapState.isSelected !== prev_selected.current[min_max])) {
        calculateTarget()
    }

    return (
        <div className="GDP">
            <FlagStat
                valueEnd={targetCountry[0]}
                country_flag={targetCountry[1]}
                heading_text={text}
            />
        </div>
    )
}

  export default MinMax
