import { useState } from 'react'
import FlagStat from "./FlagStat";

let highest_gdp = [0, '']
let gdp_array = [0]
let flag_array = ['']
let prev_country = ''
let prev_selected = false

function HighestGDP({ mapState, country_data, country_gdp }) {
    const [topCountry, setTopCountry] = useState([0, '']);

    function calculateHighestGDP() {
        prev_country = mapState.countryCode
        prev_selected = mapState.isSelected

        // console.log(mapState)
        let gdp = country_gdp.find(i=>i.country === mapState.countryName).imfGDP

        if (!mapState.isSelected) {
            let index = gdp_array.indexOf(gdp)
            gdp_array = gdp_array.filter(function(e) { return e !== gdp })
            flag_array.splice(index, 1)

            if (gdp === highest_gdp[0]) {
                setTopCountry(highest_gdp)
            }

            return
        }

        let flag = country_data.find(i=>i.cca2 === mapState.countryCode).flag
        gdp_array.push(gdp)
        flag_array.push(flag)

        if (gdp > highest_gdp[0]) {
            highest_gdp = [gdp, flag]
            setTopCountry(highest_gdp)
        }
    }

    if (mapState.countryCode !== '' && (mapState.countryCode !== prev_country || mapState.isSelected !== prev_selected)) {
        calculateHighestGDP()
    }

    return (
        <div className="HighestGDP">
            <FlagStat
                valueEnd={topCountry[0]}
                country_flag={topCountry[1]}
                heading_text={"ðŸ’¸ Highest GDP:"}
            />
        </div>
    )
}

  export default HighestGDP
